import React, { Component } from "react";
import PreviewSide from '../components/PreviewSide.js';
import AddWorkExp from './createWorkExp.js';
import data from '../data.js';



class WorkExp extends Component {

  workInput = { wordId: '',
                titleWork: '',
                companyWork: '',
                startWork: '',
                endWork: '',
                descriptionWork: '',
              };


  titleWorkChange(e){
    this.workInput.titleWork = e.target.value;
    this.forceUpdate();
    //Push to data.work Array
  }

  companyWorkChange(e){
    this.workInput.companyWork = e.target.value;
    this.forceUpdate();
    //Push to data.work Array
  }


  render() {
    return (
      <div className="workExpDiv">
          <div className="workExpTitle" id={this.idExp}>
            <label htmlFor="workExpInput" placeholder="Title"></label>
            <input 
              className="workExpInput" 
              name="title"
              // value={this.state.address || ''}
              // onChange={this.addressChange.bind(this)}
              id={this.idExp}
            />
            {/* {id !== 0 && 
              <button> delete </button>
            } */}
          </div>

          <button className="addBtn" onClick={this.addWorkExp}>Add</button>
      </div>


    )
  }
}

export default WorkExp;
