import React, { Component } from "react";
import CreateWorkExp from './createWorkExp.js';
import data from '../data.js';



class WorkExp extends Component {

  constructor() {
    super();

    this.state = {  
      workExpArray: [
        {
          index: Math.random(),
          titleWork: '',
          companyWork: '',
          startWork: '',
          endWork: '',
          descriptionWork: '',
        }
      ]
  };
};

// Manage all inputs
    handleChange = e => {
      if (
        ["titleWork", "companyWork", "startWork", "endWork", "descriptionWork"].includes(
          e.target.name
        )
      ) {
        let workExp = [...this.state.workExpArray];
        workExp[e.target.dataset.id][e.target.name] = e.target.value;
      } else {
        this.setState({ [e.target.name]: e.target.value });
      }
      console.log(this.state.workExpArray);
    };


// Add new workFields
    addNewFields = e => {
      this.setState(prevState => ({
        workExpArray: [
          ...prevState.workExpArray,
          {
            index: Math.random(),
            titleWork: '',
            companyWork: '',
            startWork: '',
            endWork: '',
            descriptionWork: '',
          }
        ]
      }));
    };

    clickOnDelete = record => {
      this.setState({
        workExpArray: this.state.workExpArray.filter(r => r !== record)
      });
      console.log(data);
    }


      // submitInput = record => {

      //   this.setState({
