      //   });

      //   data.workingExp.push(this.state.workExpArray);

      //   console.log(data);
      // }




            // deleteInputs = index => {
      //   this.setState({
      //     workExpArray: this.state.workExpArray.filter(
      //       (s, sindex) => index !== sindex
      //     )
      //   });
      // };



  render() {

    let {workExpArray} = this.state;

    return (

      <div className="workExpDiv">
        <form onSubmit={this.handleSubmit} onChange={this.handleChange}>
              <div className="container">
                  < CreateWorkExp
                    add={this.addNewFields}
                    delete={this.clickOnDelete.bind(this)}
                    // submit={this.submitInput.bind(this)}
                    workExpArray={workExpArray}
                  />
              </div>
        </form>
      </div>

    )
  }
}


export default WorkExp;
