import React, { Component } from "react";
import PreviewSide from '../components/PreviewSide.js';
import AddWorkExp from './createWorkExp.js';
import data from '../data.js';



class WorkExp extends Component {

  constructor() {
    super();

    this.state = {  
      workExpArray: [
        {
          wordId: Math.random(),
          titleWork: '',
          companyWork: '',
          startWork: '',
          endWork: '',
          descriptionWork: '',
        }
      ]
  };

// Manage all inputs
  let titleWorkChange = e => {
    this.state.workExpArray.titleWork = this.setState(prevState =>({workExpArray: {...prevState.workExpArray, titleWork: e.target.value}}));
    data.workingExp.push(this.state);
    this.forceUpdate();
  };

  let companyWorkChange = e => {
    this.state.workExpArray.companyWork = this.setState(prevState =>({workExpArray: {...prevState.workExpArray, companyWork: e.target.value}}));
    data.workingExp.push(this.state);
    this.forceUpdate();
  }


// Add new workFields
  let addNewFields = e => {
    this.setState(prevState => ({
      workExpArray: [
        ...prevState.workExpArray,
        {
          wordId: Math.random(),
          titleWork: '',
          companyWork: '',
          startWork: '',
          endWork: '',
          descriptionWork: '',
        }
      ]
    }));
    console.log(this.workExpArray);
  };
}

  render() {

    let {workExpArray} = this.state;

    return (
      <div className="workExpDiv">
          <div className="workExpTitle">
            <label htmlFor="workExpInput" placeholder="Title"></label>
            <input 
              className="workExpInput" 
              name="title"
            />
          </div>

          <button className="addBtn" onClick={this.state.addNewFields}>Add</button>
      </div>


    )
  }
}


export default WorkExp;
